
SELECT cl.nome,cl.email, p.numProcesso, p.dataProcesso, p.idAcao,ad.nomeADV, p.OAB
FROM clientes cl join pastas pa
on cl.cpf=pa.cpf 
join processos p
on p.numProcesso = pa.numProcesso
join advogados ad
on p.OAB = ad.OAB
ORDER BY (cl.nome);


SELECT T.idAcao as 'ID Ação', T.tipoAcao as 'Tipo Ação', count(distinct tipoAcao) as 'contador' 
FROM PASTAS PA JOIN CLIENTES CL
ON PA.CPF = CL.CPF
JOIN PROCESSOS P
ON PA.numProcesso = P.numProcesso
JOIN TIPOSACOES T
ON T.idAcao = P.idAcao
where tipoAcao = 'Hora-extra';

select * from tiposacoes
order by (tipoAcao);